 setContentView(R.layout.activity_main) // Replace with your layout

 handleIntent(intent)
 }

 override fun onNewIntent(intent: Intent?) {
 super.onNewIntent(intent)
 handleIntent(intent)
 }

 private fun handleIntent(intent: Intent?) {
 intent?.let {
 if (it.action == Intent.ACTION_SEND && it.type?.startsWith("image/") == true) {
 val imageUri = it.getParcelableExtra<android.net.Uri>(Intent.EXTRA_STREAM)
 imageUri?.let { uri ->
 launchFlutterActivity(uri.toString(), null)
 }
 } else if (it.action == Intent.ACTION_SEND_MULTIPLE && it.type?.startsWith("image/") == true) {
 val imageUris = it.getParcelableArrayListExtra<android.os.Parcelable>(Intent.EXTRA_STREAM) as? ArrayList<android.net.Uri>
 imageUris?.let { uris ->
 launchFlutterActivity(null, ArrayList(uris.map { it.toString() }))
 }
 } else if (it.action == Intent.ACTION_VIEW && it.data != null && it.data?.scheme != null && it.data?.type?.startsWith("image/") == true) {
 launchFlutterActivity(it.data.toString(), null)
 }
 }
 }

 private fun launchFlutterActivity(singleImageUri: String?, multipleImageUris: ArrayList<String>?) {
 val intent = FlutterActivity.createDefaultIntent(this)
 singleImageUri?.let { intent.putExtra("shared_image_uri", it) }
 multipleImageUris?.let { intent.putStringArrayListExtra("shared_image_uris", it) }
 startActivity(intent)
 }
