+ if (intent.type?.startsWith("image/") == true) {
+ // Handle multiple images being sent
+ intent.getParcelableArrayListExtra<Uri>(Intent.EXTRA_STREAM)?.let { uris ->
+ receivedImageUris = uris.toList()
+ }
+ }
+ }
+ }
+ }
